create table book_details (id number(19,0) generated as identity, crated_user varchar2(255 char), created_date timestamp, updated_date timestamp, updated_user varchar2(255 char), author varchar2(255 char), book_name varchar2(255 char), book_type varchar2(255 char), country varchar2(255 char), image_link varchar2(255 char), language varchar2(255 char), link varchar2(255 char), pages number(19,0), seller_name varchar2(255 char), year number(19,0), primary key (id));